// ESERCIZIO 1
/*
function checkAge() {
    const myAge = 32;
    const message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }
}
checkAge();

se l'età è minore di un certo valore, restituisce una stringa, altrimenti un'altra.
const dev'essere modificato in let, inoltre non esiste un return o un log in console.
*/

function checkAge() {
    let myAge = 32;
    let message = '';

    if (myAge < 18) {
        message = `Sei troppo giovane! Hai ${myAge} anni!`;
    } else {
        message = 'Hai più di 18 anni!';
    }
    console.log(message);
}
checkAge();

